pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import '../Proxy.sol';
import 'hardhat/console.sol';

contract Treasury is System {


  //////////////////////////////////////////////////////////////////////////////
  //                              SYSTEM CONFIG                               //
  //////////////////////////////////////////////////////////////////////////////


  constructor(Proxy proxy_) System(proxy_) {}

  function KEYCODE() external pure override returns (bytes3) { return "TSY"; }

  mapping(address => bool) public trackedAsset;

  mapping(address => uint256) public totalInflowsForAsset;
  mapping(address => uint256) public totalOutflowsForAsset;

  mapping(uint256 => mapping(address => uint256)) public assetInflowsPerEpoch;
  mapping(uint256 => mapping(address => uint256)) public assetOutflowsPerEpoch;


  ////////////////////////////////////////////////////////////////////////////
  //                           POLICY INTERFACE                             //
  ////////////////////////////////////////////////////////////////////////////


  event AssetAdded(address token);
  event PaymentProcessed(address from, address token, uint256 amount);
  event FundsWithdrawn(address token, uint256 amount);


  function addTrackedAsset(address token_) external onlyPolicy {
    trackedAsset[token_] = true;

    emit AssetAdded(token_);
  }


  function processPayment(address from_, address token_, uint256 amount_) external onlyPolicy {
    require(trackedAsset[token_] == true, "cannot processPayment(): token is not an accepted currency by the treasury");

    IERC20(token_).transferFrom(from_, address(this), amount_);

    uint256 epoch = _proxy.currentEpoch();
    totalInflowsForAsset[token_] += amount_;
    assetInflowsPerEpoch[epoch][token_] += amount_;

    emit PaymentProcessed(from_, token_, amount_);
  }


  function withdrawFunds(address token_, uint256 amount_) external onlyPolicy {
    require(trackedAsset[token_] == true, "cannot withdrawFunds(): token is not an accepted currency by the treasury");

    IERC20(token_).transfer(msg.sender, amount_);

    uint256 epoch = _proxy.currentEpoch();
    totalOutflowsForAsset[token_] += amount_;
    assetOutflowsPerEpoch[epoch][token_] += amount_;

    emit FundsWithdrawn(token_, amount_);
  }
}","keccak256":"0x8d7498a9231a6b880f750c52d36016b426444b7ef268080996f9e6c62cb83a25","license":"GPL-2.0"},"contracts/Systems/VTP.sol":{"content":"// SPDX-License-Identifier: GPL-2.0

pragma solidity ^0.8.10;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '../Proxy.sol';

contract VotingPower is System, IERC20 {


  //////////////////////////////////////////////////////////////////////////////
  //                              SYSTEM CONFIG                               //
  //////////////////////////////////////////////////////////////////////////////


  constructor(Proxy proxy_) System(proxy_) {}

  function KEYCODE() external pure override returns (bytes3) { return "VTP"; }

  function balanceOf(address wallet_) public view override returns (uint256) {
    return _baseBalanceOf[wallet_] * currentIndex / 1e6;
  }

  function totalSupply() public view override returns (uint256) {
    return _totalBaseSupply * currentIndex / 1e6;
  }

  // brick the allowance features for the token
  function allowance(address, address) external pure override returns (uint256) {
    return type(uint256).max;
  }

  function approve(address, uint256) external pure override returns (bool) {
    return true;
  }

  // disable transfer of tokens from wallets. Voting power is directly issued to address and stays there until redemption.
  function transferFrom(address, address, uint256) external pure override returns(bool) {
    assert(false);
    return true;
  }

  // restrict EOA transfers.
  function transfer(address, uint256) public pure override returns (bool) {
    assert(false);
    return true;
  }


  /////////////////////////////////////////////////////////////////////////////////
  //                              System Variables                               //
  /////////////////////////////////////////////////////////////////////////////////


  string public name = "PR0XY Voting Power";
  string public symbol = "vePROX";
  uint8 public decimals = 3;

  uint256 public currentIndex = 1e6; // rebase multiplier on base, with 6 decimals of precision
  uint256 private _totalBaseSupply = 0;
  mapping(address => uint256) private _baseBalanceOf;

  uint16 public vestingTerm = 15;
  mapping(address => uint16) public vestingCreditsOf;


  ////////////////////////////////////////////////////////////////////////////
  //                           POLICY INTERFACE                             //
  ////////////////////////////////////////////////////////////////////////////


  // event Transfer(address from, address to, uint256 amount) => declared in the imported IERC20.sol
  event Rebased(uint256 basisPoints);
  event VestingCreditsIncremented(address wallet);
  event VestingCreditsReset(address wallet);


  function rebase(uint256 basisPoints_) external onlyPolicy {
    currentIndex = currentIndex * (10000 + basisPoints_) / 1e4;

    emit Rebased(basisPoints_);
  }


  function issue(address to_, uint256 amount_) external onlyPolicy returns (uint256) {
    uint256 baseAmt = amount_ * (1e6) / currentIndex;

    vestingCreditsOf[msg.sender] = 0;
    _totalBaseSupply += baseAmt;
    _baseBalanceOf[to_] += baseAmt;

    emit Transfer(address(0), to_, amount_);

    return baseAmt;
  }


  function redeem(address from_, uint256 amount_) external onlyPolicy returns(uint256) {
    uint256 baseAmt = amount_ * 1e6 / currentIndex;

    vestingCreditsOf[msg.sender] = 0;
    _baseBalanceOf[from_] -= baseAmt;
    _totalBaseSupply -= baseAmt;

    emit Transfer(from_, address(0), amount_);

    return baseAmt;
  }


  function resetVestingCredits(address wallet_) external onlyPolicy {
    vestingCreditsOf[wallet_] = 0;

    emit VestingCreditsReset(wallet_);
  }


  function incrementVestingCredits(address wallet_) external onlyPolicy {
    vestingCreditsOf[wallet_]++;

    emit VestingCreditsIncremented(wallet_);
  }
